import axios from 'axios';

export const getAllPizzas = () => dispatch => {

    dispatch({
        type: 'Get_PIZZAS_REQUEST'
    })

    try {
        const response = axios.get('/api/pizzas')
        console.log(response)
        dispatch({
            type: 'Get_PIZZAS_SUCCESS', payload : response.data
        })

    } catch (error) {
        dispatch({
            type: 'Get_PIZZAS_FAIL',  payload : error
        })
    }
}
